import { useEffect, useState } from "react";
import axios from "axios";

import styles from "./Heroes.module.css";

const Heroes = () => {
  const [heroes, setHeroes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const ALLOWED_HEROES = [
  "Abrams",
  "Warden",
  "Vindicta",
  "Grey Talon",
  "Infernus",
  "Seven",
  "Lady Geist",
  "Wraith",
  "Mcginnis",
  "Paradox",
  "Dynamo",
  "Kelvin",
  "Haze",
  "Holliday",
  "Bebop",
  "Calico",
  "Mo & Krill",
  "Shiv",
  "Ivy",
  "Vyper",
  "Yamato",
  "Lash",
  "Mirage",
  "Mina",
  "Drifter",
  "Viscous",
  "Pocket",
  "Victor",
  "Sinclair",
  "Paige",
  "The Doorman",
  "Billy"
];


  useEffect(() => {
    const fetchHeroes = async () => {
      try {
        const { data } = await axios.get(
          "https://assets.deadlock-api.com/v2/heroes"
        );

        setHeroes(data);
      } catch (err) {
        setError("Failed to load heroes");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchHeroes();
  }, []);

  if (loading) return <p>Loading heroes...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className={styles.heroes_grid}>
      {heroes.map((hero) => (
        <div key={hero.id} className={styles.hero_card}>
          <img
            src={hero.images?.minimap_image}
            alt={hero.name}
            className={styles.heroImage}
          />
          <h3>{hero.name}</h3>
        </div>
      ))}
    </div>
  );
};

export default Heroes;
